import React, { useState } from "react";
import axios from "axios";
import "./Scripts.css";

const Scripts = () => {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState("");
  const [scriptType, setScriptType] = useState("sqli");

  const handleRun = async () => {
    try {
      const response = await axios.post("http://localhost:5000/run-script", {
        url,
        scriptType,
      });
      setResult(response.data.result);
    } catch (err) {
      setResult("Error running script.");
    }
  };

  return (
    <div className="scripts-container">
      <h1>Python Security Scripts</h1>

      <div className="info-section">
        <h2>OWASP SQL Injection</h2>
        <p>SQLi occurs when attacker can inject SQL into input fields or URL.</p>

        <h2>OWASP XSS (Cross-Site Scripting)</h2>
        <p>XSS allows attacker to inject malicious scripts into trusted websites.</p>
      </div>

      <div className="form-section">
        <input
          type="text"
          placeholder="Enter URL to test"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />

        <select value={scriptType} onChange={(e) => setScriptType(e.target.value)}>
          <option value="sqli">SQL Injection Detection</option>
          <option value="xss">XSS Detection</option>
        </select>

        <button onClick={handleRun}>Run Script</button>
      </div>

      {result && <div className="result-box">{result}</div>}
    </div>
  );
};

export default Scripts;
